-- Procedure structure for usp_CalculateMonthlySalary
-- ----------------------------
DROP PROCEDURE IF EXISTS `usp_CalculateMonthlySalary`;
delimiter ;;
CREATE PROCEDURE `usp_CalculateMonthlySalary`(IN p_month INT,
                IN p_year INT)
BEGIN
                DECLARE v_total_customers INT;
                DECLARE v_bonus_coefficient DECIMAL(5,2);
                DECLARE v_base_salary DECIMAL(15,2);
                DECLARE v_employee_id INT;
                DECLARE done INT DEFAULT FALSE;
                DECLARE employee_cursor CURSOR FOR
                    SELECT employee_id FROM employees;
                DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = TRUE;

                -- Count total customers in month
                SELECT COUNT(DISTINCT customer_id) INTO v_total_customers
                FROM orders
                WHERE MONTH(initial_order_time) = p_month
                AND YEAR(initial_order_time) = p_year
                AND order_status = 'hoàn tất';

                -- Calculate bonus coefficient
                SET v_bonus_coefficient = FLOOR(v_total_customers / 10) * 0.02;

                -- Process each employee
                OPEN employee_cursor;
                read_loop: LOOP
                    FETCH employee_cursor INTO v_employee_id;
                    IF done THEN
                        LEAVE read_loop;
                    END IF;

                    -- Get base salary
                    SELECT base_salary INTO v_base_salary
                    FROM employees e
                    JOIN employee_types et ON e.employee_type_id = et.employee_type_id
                    WHERE e.employee_id = v_employee_id;

                    -- Insert or update salary record
                    VALUES (
                        v_employee_id,
                        p_month,
                        p_year,
                        v_base_salary * v_bonus_coefficient
                    )
                    ON DUPLICATE KEY UPDATE
                    bonus_amount = v_base_salary * v_bonus_coefficient;
                END LOOP;
                CLOSE employee_cursor;
            END
;;
delimiter ;

-- ----------------------------
-- Procedure structure for usp_Checkout
-- ----------------------------
DROP PROCEDURE IF EXISTS `usp_Checkout`;
delimiter ;;
CREATE PROCEDURE `usp_Checkout`(IN p_order_id INT,
                IN p_payment_method ENUM('Tiền mặt', 'Chuyển khoản', 'Thẻ tín dụng', 'Ví điện tử'),
                IN p_employee_id INT,
                IN p_discount DECIMAL(5,2))
BEGIN
                DECLARE v_total_amount DECIMAL(15,2);
                DECLARE v_table_id INT;

                -- Calculate total amount
                SELECT SUM(quantity * unit_price) INTO v_total_amount
                FROM order_details
                WHERE order_id = p_order_id;

                -- Create bill
                VALUES (p_order_id, p_payment_method, p_employee_id, p_discount);

                -- Get table_id from order
                SELECT table_id INTO v_table_id FROM orders WHERE order_id = p_order_id;

                -- Update table status if dine-in order
                IF v_table_id IS NOT NULL THEN
                    UPDATE dining_tables
                    SET table_status = 'Đang dọn dẹp'
                    WHERE table_id = v_table_id;
                END IF;

                SELECT v_total_amount * (100 - p_discount) / 100 AS final_amount;
            END
;;
delimiter ;

-- ----------------------------
-- Procedure structure for usp_PlaceOrder
-- ----------------------------
DROP PROCEDURE IF EXISTS `usp_PlaceOrder`;
delimiter ;;
CREATE PROCEDURE `usp_PlaceOrder`(IN p_table_id INT,
                IN p_number_of_guests INT,
                IN p_customer_id INT,
                IN p_employee_id INT,
                IN p_order_type ENUM('Tại bàn', 'Mang về'),
                IN p_dishes JSON)
proc_label: BEGIN
                DECLARE v_order_id INT;
                DECLARE v_reserved_table_id INT;
                DECLARE v_table_status VARCHAR(50);
                DECLARE i INT DEFAULT 0;
                DECLARE dish_count INT;
                DECLARE v_dish_id VARCHAR(10);
                DECLARE v_quantity INT;
                DECLARE v_capacity INT;
                DECLARE v_existing_order_id INT;

                -- Handle takeaway orders
                IF p_order_type = 'Mang về' THEN
                    -- Check for existing active takeaway order
                    SELECT order_id INTO v_existing_order_id
                    FROM orders
                    WHERE customer_id = p_customer_id
                    AND order_type = 'Mang về'
                    AND order_status IN ('đã tiếp nhận', 'đang thực hiện')
                    LIMIT 1;

                    IF v_existing_order_id IS NOT NULL THEN
                        SET v_order_id = v_existing_order_id;
                    ELSE
                        -- Create new takeaway order
                        VALUES (NULL, p_employee_id, p_customer_id, p_order_type);
                        SET v_order_id = LAST_INSERT_ID();
                    END IF;

                    -- Add dishes to order
                    SET dish_count = JSON_LENGTH(p_dishes);
                    WHILE i < dish_count DO
                        SET v_dish_id = JSON_UNQUOTE(JSON_EXTRACT(p_dishes, CONCAT('$[', i, '].dish_id')));
                        SET v_quantity = JSON_EXTRACT(p_dishes, CONCAT('$[', i, '].quantity'));

                        -- Check if dish already exists in order
                        IF EXISTS (SELECT 1 FROM order_details WHERE order_id = v_order_id AND dish_id = v_dish_id) THEN
                            UPDATE order_details
                            SET quantity = quantity + v_quantity,
                                order_dish_status = 'Tiếp nhận',
                                dish_order_time = CURRENT_TIMESTAMP
                            WHERE order_id = v_order_id AND dish_id = v_dish_id;
                        ELSE
                            VALUES (v_order_id, v_dish_id, v_quantity);
                        END IF;

                        SET i = i + 1;
                    END WHILE;

                    SELECT v_order_id AS order_id;
                    LEAVE proc_label;
                END IF;

                -- Handle dine-in orders (simplified version)
                IF p_order_type = 'Tại bàn' THEN
                    -- Create new order
                    VALUES (p_table_id, p_employee_id, p_customer_id, p_order_type);
                    SET v_order_id = LAST_INSERT_ID();

                    -- Update table status
                    UPDATE dining_tables
                    SET table_status = 'Đang phục vụ'
                    WHERE table_id = p_table_id;

                    -- Add dishes to order
                    SET dish_count = JSON_LENGTH(p_dishes);
                    WHILE i < dish_count DO
                        SET v_dish_id = JSON_UNQUOTE(JSON_EXTRACT(p_dishes, CONCAT('$[', i, '].dish_id')));
                        SET v_quantity = JSON_EXTRACT(p_dishes, CONCAT('$[', i, '].quantity'));

                        VALUES (v_order_id, v_dish_id, v_quantity);

                        SET i = i + 1;
                    END WHILE;

                    SELECT v_order_id AS order_id;
                END IF;
            END
;;
delimiter ;

