-- ----------------------------
-- Triggers structure for table order_details
-- ----------------------------
DROP TRIGGER IF EXISTS `TRG_BeforeInsertOrderDetail`;
delimiter ;;
CREATE TRIGGER `restaurant_management_v3`.`TRG_BeforeInsertOrderDetail` BEFORE INSERT ON `order_details` FOR EACH ROW BEGIN
                DECLARE dish_price DECIMAL(15,2);
                DECLARE required_quantity DECIMAL(10,2);
                DECLARE current_stock DECIMAL(10,2);
                DECLARE ingredient_id_val INT;
                DECLARE done INT DEFAULT FALSE;
                DECLARE recipe_cursor CURSOR FOR
                    SELECT ingredient_id, quantity
                    FROM recipes
                    WHERE dish_id = NEW.dish_id;
                DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = TRUE;

                -- Get dish price
                SELECT price INTO dish_price FROM dishes WHERE dish_id = NEW.dish_id;
                SET NEW.unit_price = dish_price;

                -- Check ingredient availability
                OPEN recipe_cursor;
                read_loop: LOOP
                    FETCH recipe_cursor INTO ingredient_id_val, required_quantity;
                    IF done THEN
                        LEAVE read_loop;
                    END IF;
                    SELECT current_quantity INTO current_stock
                    FROM ingredients
                    WHERE ingredient_id = ingredient_id_val;
                    IF current_stock < (required_quantity * NEW.quantity) THEN
                        SIGNAL SQLSTATE '45000'
                        SET MESSAGE_TEXT = 'Không đủ nguyên liệu trong kho';
                    END IF;
                END LOOP;
                CLOSE recipe_cursor;
            END
;;
delimiter ;

-- ----------------------------
-- Triggers structure for table order_details
-- ----------------------------
DROP TRIGGER IF EXISTS `TRG_AfterInsertOrderDetail`;
delimiter ;;
CREATE TRIGGER `restaurant_management_v3`.`TRG_AfterInsertOrderDetail` AFTER INSERT ON `order_details` FOR EACH ROW BEGIN
                DECLARE required_quantity DECIMAL(10,2);
                DECLARE ingredient_id_val INT;
                DECLARE done INT DEFAULT FALSE;
                DECLARE recipe_cursor CURSOR FOR
                    SELECT ingredient_id, quantity
                    FROM recipes
                    WHERE dish_id = NEW.dish_id;
                DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = TRUE;

                -- Deduct ingredients from inventory
                OPEN recipe_cursor;
                read_loop: LOOP
                    FETCH recipe_cursor INTO ingredient_id_val, required_quantity;
                    IF done THEN
                        LEAVE read_loop;
                    END IF;
                    UPDATE ingredients
                    SET current_quantity = current_quantity - (required_quantity * NEW.quantity)
                    WHERE ingredient_id = ingredient_id_val;
                END LOOP;
                CLOSE recipe_cursor;
            END
;;
delimiter ;

